import numpy as np
import matplotlib.pyplot as plt
import cv2
import urllib.request


class EdgeDetector:
    horizontal_filter = np.array([[-1, 1]])
    vertical_filter = np.array([[-1], [1]])

    @staticmethod
    def apply_filters(gray_image):
        horizontal_derivative = cv2.filter2D(gray_image, -1, EdgeDetector.horizontal_filter)
        vertical_derivative = cv2.filter2D(gray_image, -1, EdgeDetector.vertical_filter)
        return horizontal_derivative + vertical_derivative


# Belirtilen URL
# url = "data:image/png;base64,"
url = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMWEA7iPSnY_9MfsoHrVrMMHZ54aA0jdOcEg&s"
# Üçgen : data:image/png;base64,
# Kare : https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMWEA7iPSnY_9MfsoHrVrMMHZ54aA0jdOcEg&s


resp = urllib.request.urlopen(url)
image_array = np.asarray(bytearray(resp.read()), dtype="uint8")
original_image = cv2.imdecode(image_array, cv2.IMREAD_COLOR)

if original_image is None:
    print("Image not found.")
else:

    gray_image = cv2.cvtColor(original_image, cv2.COLOR_BGR2GRAY)

    edge_detected_image = EdgeDetector.apply_filters(gray_image)

    fig, axs = plt.subplots(1, 2, figsize=(10, 5))

    axs[0].imshow(cv2.cvtColor(original_image, cv2.COLOR_BGR2RGB))
    axs[0].set_title('Original Image')
    axs[0].axis('off')

    axs[1].imshow(edge_detected_image, cmap='gray')
    axs[1].set_title('Edge Detected Image')
    axs[1].axis('off')

    plt.show()